40, 41]
const split = 7

function App() {


  const [ red, setRed ] = useState(true)

  const enter = (index) => {

    for (let i = 0; i <= 5; i++) {
      {console.log(ItemArray[split*i+index])}
    }

    setRed(!red)
    // console.log(`circle ${red ? 'red' : 'yellow'}`)
    ItemArray[index] = `circle ${red ? 'red' : 'yellow'}`
    // console.log(ItemArray[index])
  }


  return (
    <div className="App">
      <header className="App-header">
        <div className='game'>
        {
          new Array(7).fill('enter-button').map( (i,index) => {
            return (<div onClick={ () => {
              setRed(() => {
                enter(index)
              })

            } }>
              <Button title='' />
            </div>)
          })
        }
          {
            ItemArray.map( (item, index) => {
              return <Icon title={item} key={index} className={item}/>
            })
          }
        </div>
      </header>
    </div>
  );
}


export default App;
