import { toast} from 'react-toastify'
import React from 'react'


const Icon = ({title='Null',className=''}) => (
    <div>
        <div className={'circle '+className}>
            {/* {title} */}
        </div>
    </div>
)


const Button = ({title='Null'}) => (
    <div className='enter-button' title='drop your discs?'>
        {title}
    </div>
)


const ReloadButton = () => {
    return (
        <button className='reload-button button'>
            Reload
        </button>
    )
}


const Message = ({type, message}) => {
    console.log('FROM MESSAGE ',type, message)
    switch (type) {
        case 'success':
            return toast(message,{
                type: type ,
                position: toast.POSITION.BOTTOM_RIGHT
            })
        case 'error':
            return toast(message,{
                type: type ,
                position: toast.POSITION.BOTTOM_RIGHT 
            })
        default:
            return toast(message,{
                type: '' ,
                position: toast.POSITION.BOTTOM_RIGHT ,
                className:'game-message toast',
                autoClose: 8000                
            })
            break;
    }
}


const InfoButton = () => {
    return (
        <div className='information-zone'>
            <button className='information' onClick={() => {
          console.log('hello')
            return toast(`Connect 4 of your disks in any direction a row, a column, or a digonal`, {
                className: 'full-row-toast toast',
                position: toast.POSITION.BOTTOM_RIGHT,
                autoClose: 10000
            })
        }}>
                Rules?
            </button>
            <ReloadButton/>
        </div>
    )
}

// const ReloadButton


export { Icon, Button, Message , InfoButton , ReloadButton};
